UPDATE public.subsidy_stage_uis
    SET input_ui = '{"type":"CustomPageNavigationControl","elements":[{"type":"CustomPageControl","label":"start","elements":[{"type":"FormGroupControl","options":{"section":true},"elements":[{"type":"FormHtml","options":{"html":"<p class=\"warning\">\n    <span>Waarschuwing:<\/span>\n    Het invullen van een aanvraag kost ongeveer 10 minuten. U kunt uw aanvraag tussentijds opslaan. Zorg ervoor dat u\n    alle gevraagde documenten digitaal bij de hand heeft. Dit kan bijvoorbeeld een scan, schermafdruk of foto vanaf uw\n    mobiele telefoon zijn. Laat uw aanvraag niet langer dan noodzakelijk open staan op uw computer.\n<\/p>\n\n<h2>Introductie<\/h2>\n<p>\n    Voor artsen in opleiding tot Arts Internationale Gezondheid en Tropengeneeskunde brengt de eindstage van 6 maanden\n    in het buitenland hoge kosten met zich mee. De subsidie Opleidingsactiviteiten AIGT 2021-2026 compenseert deze\n    kosten.\n<\/p>\n<p>\n    De subsidie is een eenmalige vaste bijdrage voor de kosten die de artsen maken tijdens het opleidingsonderdeel\n    Buitenland. Het gaat hierbij bijvoorbeeld om reis- en verblijfkosten, visa, werkvergunning, inentingen,\n    verzekeringen en communicatiemiddelen.\n<\/p>\n<h2>Belangrijke voorwaarden<\/h2>\n<p><\/p>\n<ol>\n    <li><b>Alleen onderdeel Buitenland van AIGT komt in aanmerking<\/b>\n        <p>\n            Alleen het opleidingsonderdeel Buitenland van de opleiding tot Arts Internationale Gezondheid en\n            Tropengeneeskunde komt in aanmerking voor deze subsidie. Andere (medische vervolg)opleidingen met soortgelijke\n            opleidingsactiviteiten komen niet in aanmerking.\n        <\/p>\n    <\/li>\n    <li>\n        <b>Bewijs van opleidingsregister en van opleidingsinstituut nodig<\/b>\n        <p>Bij de aanvraag levert u de volgende documenten in:<\/p>\n        <ol>\n            <li>\n                bewijs van inschrijving in het opleidingsregister\n            <\/li>\n            <li>\n                bewijs dat u de opleiding volgt of heeft gevolgd\n            <\/li>\n        <\/ol>\n    <\/li>\n<\/ol>\n<p>Laat uw aanvraag niet langer dan noodzakelijk open staan op uw computer.<\/p>\n<h2>Aanvraag starten<\/h2>\n"}}]}],"options":{"required":[],"allOf":[]}},{"type":"CustomPageControl","label":"Persoonsgegevens toevoegen","elements":[{"type":"FormGroupControl","options":{"section":true},"elements":[{"type":"Group","label":"Persoonlijke informatie","elements":[{"type":"VerticalLayout","elements":[{"type":"CustomControl","scope":"#\/properties\/firstName","label":"Voornaam","options":{"placeholder":""}},{"type":"CustomControl","scope":"#\/properties\/infix","label":"Tussenvoegsel","options":{"placeholder":""}},{"type":"CustomControl","scope":"#\/properties\/lastName","label":"Achternaam","options":{"placeholder":""}}]}]},{"type":"Group","label":"Adres","elements":[{"type":"VerticalLayout","elements":[{"type":"CustomControl","scope":"#\/properties\/street","label":"Straatnaam","options":{"placeholder":""}},{"type":"CustomControl","scope":"#\/properties\/houseNumber","label":"Huisnummer","options":{"placeholder":""}},{"type":"CustomControl","scope":"#\/properties\/houseNumberSuffix","label":"Huisnummer toevoeging","options":{"placeholder":""}},{"type":"CustomControl","scope":"#\/properties\/postalCode","label":"Postcode","options":{"placeholder":""}},{"type":"CustomControl","scope":"#\/properties\/city","label":"Plaatsnaam","options":{"placeholder":""}},{"type":"CustomControl","scope":"#\/properties\/country","label":"Land","options":{"format":"select","placeholder":"Selecteer een land"}}]}]},{"type":"Group","label":"Contact","elements":[{"type":"VerticalLayout","elements":[{"type":"CustomControl","scope":"#\/properties\/phoneNumber","label":"Telefoonnummer","options":{"placeholder":""}},{"type":"CustomControl","scope":"#\/properties\/email","label":"E-mailadres","options":{"placeholder":"","tip":"U wordt via dit e-mailadres ge\u00efnformeerd over de status van uw aanvraag. Geef daarom alleen uw eigen e-mailadres door.","validation":["onBlur"]}}]}]},{"type":"Group","label":"Bank","elements":[{"type":"VerticalLayout","elements":[{"type":"CustomControl","scope":"#\/properties\/bankAccountHolder","label":"Naam rekeninghouder","options":{"placeholder":"","validation":["onBlur"]}},{"type":"CustomControl","scope":"#\/properties\/bankAccountNumber","label":"IBAN","options":{"placeholder":"","tip":"Staat u onder bewind? Vermeld dan het IBAN van uw beheerrekening.","validation":["onValid"]}}]}]}]}],"options":{"required":["firstName","lastName","street","houseNumber","postalCode","city","country","phoneNumber","email","bankAccountHolder","bankAccountNumber"],"allOf":[]}},{"type":"CustomPageControl","label":"Opleidingsgegevens","elements":[{"type":"FormGroupControl","options":{"section":true},"elements":[{"type":"Group","label":"Opleiding","elements":[{"type":"VerticalLayout","elements":[{"type":"CustomControl","scope":"#\/properties\/abroadCourseComponentStartDate","label":"Start opleidingsonderdeel buitenland","options":{"placeholder":""}}]}]},{"type":"Group","label":"Documenten","elements":[{"type":"VerticalLayout","elements":[{"type":"CustomControl","scope":"#\/properties\/proofOfRegistrationDocument","label":"Bewijs van inschrijving","options":{"accept":"image\/jpeg,image\/png,.pdf","maxFileSize":20971520,"minItems":1,"maxItems":20,"tip":"Upload de brief met het besluit van de Registratiecommissie Geneeskundig Specialisten (RGS) waarin wordt bevestigd dat u bent ingeschreven in het opleidingsregister voor het specialisme profielregister Internationale Gezondheidszorg en Tropengeneeskunde.\nDe bijlage bij de brief moet u niet indienen. Het overzicht met registratiegegevens verwerken wij namelijk niet bij de behandeling van de aanvraag. Toegestane bestandstypen: pdf, jpg, jpeg, png. Maximale bestandsgrootte: 20 MB."}}]},{"type":"VerticalLayout","elements":[{"type":"CustomControl","scope":"#\/properties\/educationalInstituteDeclarationDocument","label":"Start opleidingsonderdeel buitenland","options":{"accept":"image\/jpeg,image\/png,.pdf","maxFileSize":20971520,"minItems":1,"maxItems":20,"tip":"Upload een ondertekende verklaring van het Opleidingsinstituut Internationale Gezondheidszorg en Tropengeneeskunde (OIGT) waarin wordt bevestigd dat u in opleiding bent tot Arts IGT-KNMG en dat u het opleidingsonderdeel \u2018Buitenland\u2019 volgt of heeft gevolgd. Toegestane bestandstypen: pdf, jpg, jpeg, png. Maximale bestandsgrootte: 20 MB."}}]}]}]}],"options":{"required":["abroadCourseComponentStartDate","proofOfRegistrationDocument","educationalInstituteDeclarationDocument"],"allOf":[]}},{"type":"CustomPageControl","label":"Controleren en ondertekenen","elements":[{"type":"FormGroupControl","options":{"section":true},"elements":[{"type":"Group","label":"Controleren","elements":[{"type":"FormResultsTable","label":"Uw gegevens","options":{"fields":{"Naam":"{firstName} {infix} {lastName}","Adres":"{street} {houseNumber}{houseNumberSuffix} {postalCode} {city}","Telefoon":"{phoneNumber}","E-mailadres":"{email}"}}}]},{"type":"Group","label":"Ondertekenen","elements":[{"type":"CustomControl","scope":"#\/properties\/truthfullyCompleted","label":"Waarheidsverklaring","options":{"description":"Ik verklaar het formulier naar waarheid te hebben ingevuld."}}]}]}],"options":{"required":["truthfullyCompleted"],"allOf":[]}}]}',
