-- subsidy_stage_uis
INSERT INTO public.subsidy_stage_uis (id, subsidy_stage_id, version, status, input_ui, created_at, updated_at, view_ui) VALUES ('422cb32a-fed3-4d69-91ca-818db6b96daf', 'd7f38409-6805-408c-87e9-afd9b00a8de0', 1, 'published', '{"type":"CustomPageNavigationControl","elements":[{"type":"CustomPageControl","label":"Start","elements":[{"type":"FormGroupControl","options":{"section":true},"elements":[{"type":"FormHtml","options":{"html":"<h2>Introductie<\/h2>\n<p>\n    Met dit formulier vraagt u een financi\u00eble ondersteuning aan via de regeling Zorgmedewerkers met langdurige post-COVID klachten. U kunt de financi\u00eble ondersteuning aanvragen als u voldoet aan de voorwaarden uit de regeling. De financi\u00eble ondersteuning bedraagt \u20ac24.000 per persoon.\n<\/p>\n<h2>Benodigde bestanden<\/h2>\n<p>Om in aanmerking te komen voor de financi\u00eble ondersteuning, vragen we u om een aantal documenten:<\/p>\n<p><\/p>\n<ol>\n    <li>Gewaarmerkt verzekeringsbericht UWV<\/li>\n    <li>Kopie WIA-beslissing<\/li>\n    <li>Bewijs van uw dienstverband<\/li>\n    <li>Medisch onderzoeksverslag \/ medische rapportage<\/li>\n<\/ol>\n\n<p>In de laatste stap van het aanvraagformulier kunt u de gevraagde documenten uploaden. In deze stap tonen we ook of mogelijk extra gegevens en\/of bewijsstukken noodzakelijk zijn voor de beoordeling van uw aanvraag. Let op: als we u om extra documenten vragen, gebruik dan altijd de <a\n    title=\"Documenten over Zorgmedewerkers met langdurige post-COVID klachten\"\n    href=\"https:\/\/www.dus-i.nl\/subsidies\/zorgmedewerkers-met-langdurige-post-covid-klachten\/documenten\"\n    target=\"_blank\"\n    rel=\"nofollow noopener external\"\n>verplichte formats<\/a\n>.<\/p>\n\n<h2>Meer informatie<\/h2>\n\n<p>Bekijk voor meer informatie over de regeling, de voorwaarden en de aanvraagprocedure onze <a title=\"website voor zorgmedewerkers met langdurige post-COVID klachten\" href=\"https:\/\/www.dus-i.nl\/post-covid\" target=\"_blank\" rel=\"nofollow noopener external\">website<\/a>. Heeft u een vraag? Gebruik dan het <a\n    title=\"Contactformulier DUS-i post-COVID\"\n    href=\"https:\/\/formulierdus-i.nl\/contact\/?kenmerk=PostCov&dus%2Di%5Furl=https%3A%2F%2Fwww%2Edus%2Di%2Enl%2Fsubsidies%2Fzorgmedewerkers%2Dmet%2Dlangdurige%2Dpost%2Dcovid%2Dklachten\"\n    target=\"_blank\"\n    rel=\"nofollow noopener external\"\n>contactformulier<\/a\n> of bel met 070-3405566. Wij zijn telefonisch bereikbaar op werkdagen van 9.00 tot 16.00 uur.<\/p>\n\n<h2>Aanvraag starten<\/h2>\n"}}]}],"options":{"required":[],"allOf":[]}},{"type":"CustomPageControl","label":"Persoonsgegevens toevoegen","elements":[{"type":"FormGroupControl","options":{"section":true},"elements":[{"type":"Group","label":"Persoonlijke informatie","elements":[{"type":"VerticalLayout","elements":[{"type":"CustomControl","scope":"#\/properties\/firstName","label":"Voornaam","options":{"placeholder":""}},{"type":"CustomControl","scope":"#\/properties\/infix","label":"Tussenvoegsel","options":{"placeholder":""}},{"type":"CustomControl","scope":"#\/properties\/lastName","label":"Achternaam","options":{"placeholder":""}},{"type":"CustomControl","scope":"#\/properties\/dateOfBirth","label":"Geboortedatum","options":{"placeholder":""}}]}]},{"type":"Group","label":"Adres","elements":[{"type":"VerticalLayout","elements":[{"type":"CustomControl","scope":"#\/properties\/street","label":"Straatnaam","options":{"placeholder":""}},{"type":"CustomControl","scope":"#\/properties\/houseNumber","label":"Huisnummer","options":{"placeholder":""}},{"type":"CustomControl","scope":"#\/properties\/houseNumberSuffix","label":"Huisnummer toevoeging","options":{"placeholder":""}},{"type":"CustomControl","scope":"#\/properties\/postalCode","label":"Postcode","options":{"placeholder":""}},{"type":"CustomControl","scope":"#\/properties\/city","label":"Plaatsnaam","options":{"placeholder":""}},{"type":"CustomControl","scope":"#\/properties\/country","label":"Land","options":{"format":"selec