jpg, jpeg, png. Maximale bestandsgrootte: 20 MB."},"rule":{"effect":"SHOW","condition":{"scope":"#\/properties\/isWiaDecisionPostponed","schema":{"const":"Ja"}}}}]}]},{"type":"Group","label":"Werkgever","elements":[{"type":"VerticalLayout","elements":[{"type":"CustomControl","scope":"#\/properties\/employmentContract","label":"Bewijs van uw dienstverband","options":{"accept":"image\/jpeg,image\/png,.pdf","maxFileSize":20971520,"minItems":1,"maxItems":20,"tip":"Toegestane bestandstypen: pdf, jpg, jpeg, png. Maximale bestandsgrootte: 20 MB."}}]},{"type":"VerticalLayout","elements":[{"type":"CustomControl","scope":"#\/properties\/employmentFunction","label":"Functie","options":{"format":"select","placeholder":"-- Selecteer een functie --"}}]},{"type":"CustomControl","scope":"#\/properties\/otherEmploymentFunction","label":"Andere functie","options":{"placeholder":""},"rule":{"effect":"SHOW","condition":{"scope":"#\/properties\/employmentFunction","schema":{"const":"Anders"}}}},{"type":"VerticalLayout","elements":[{"type":"CustomControl","scope":"#\/properties\/employerKind","label":"Werkgever","options":{"format":"radio"}}]},{"type":"VerticalLayout","elements":[{"type":"CustomControl","scope":"#\/properties\/otherEmployerDeclarationFile","label":"Verklaring zorgaanbieder","options":{"accept":"image\/jpeg,image\/png,.pdf","maxFileSize":20971520,"minItems":1,"maxItems":20,"tip":"Toegestane bestandstypen: pdf, jpg, jpeg, png. Maximale bestandsgrootte: 20 MB."},"rule":{"effect":"SHOW","condition":{"scope":"#","schema":{"anyOf":[{"required":["employmentFunction"],"properties":{"employmentFunction":{"const":"Anders"}}},{"required":["employerKind"],"properties":{"employerKind":{"const":"Andere organisatie"}}}]}}}}]},{"type":"VerticalLayout","elements":[{"type":"CustomControl","scope":"#\/properties\/hasBeenWorkingAtJudicialInstitution","label":"Heeft u gewerkt in een justiti\u00eble inrichting?","options":{"format":"radio"}}]},{"type":"VerticalLayout","elements":[{"type":"CustomControl","scope":"#\/properties\/BIGNumberJudicialInstitution","label":"BIG-nummer","options":{},"rule":{"effect":"SHOW","condition":{"scope":"#\/properties\/hasBeenWorkingAtJudicialInstitution","schema":{"const":"Ja"}}}}]}]},{"type":"Group","label":"Medisch","elements":[{"type":"VerticalLayout","elements":[{"type":"CustomControl","scope":"#\/properties\/socialMedicalAssessment","label":"Medisch onderzoeksverslag (medische rapportage)","options":{"accept":"image\/jpeg,image\/png,.pdf","maxFileSize":20971520,"minItems":1,"maxItems":20,"tip":"Toegestane bestandstypen: pdf, jpg, jpeg, png. Maximale bestandsgrootte: 20 MB."}}]},{"type":"VerticalLayout","elements":[{"type":"CustomControl","scope":"#\/properties\/hasPostCovidDiagnose","label":"Bevat uw medisch onderzoeksverslag de diagnose langdurige post-COVID?","options":{"format":"radio"}}]},{"type":"VerticalLayout","elements":[{"type":"CustomControl","scope":"#\/properties\/doctorsCertificate","label":"Verklaring arts","options":{"accept":"image\/jpeg,image\/png,.pdf","maxFileSize":20971520,"minItems":1,"maxItems":20,"tip":"Toegestane bestandstypen: pdf, jpg, jpeg, png. Maximale bestandsgrootte: 20 MB."},"rule":{"effect":"SHOW","condition":{"scope":"#\/properties\/hasPostCovidDiagnose","schema":{"const":"Nee"}}}}]}]}]}],"options":{"required":["certifiedEmploymentDocument","wiaDecisionDocument","isWiaDecisionPostponed","employmentContract","employmentFunction","employerKind","hasBeenWorkingAtJudicialInstitution","socialMedicalAssessment","hasPostCovidDiagnose"],"allOf":[{"if":{"properties":{"isWiaDecisionPostponed":{"const":"Ja"}}},"then":{"required":["wiaDecisionPostponedLetter"]}},{"if":{"anyOf":[{"required":["employmentFunction"],"properties":{"employmentFunction":{"const":"Anders"}}},{"required":["employerKind"],"properties":{"employerKind":{"const":"Andere organisatie"}}}]},"then":{"required":["otherEmployerDeclarationFile"]}},{"if":{"properties":{"hasBeenWorkingAtJudicialInstitution":{"const":"Ja"}}},"then":{"